import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
const BASE_URL = process.env.BASE_URL

const Login = (props) => {
    const [credentials , setCredentials] = useState({email:"" , password:""})
    let navigate = useNavigate()
    const handleSubmit = async (e) =>{
        e.preventDefault()
        const response = await fetch("$/api/auth/login", {
            method: 'POST',
            headers: {
              'content-Type': 'application/json',
            },
            body: JSON.stringify({email:credentials.email,password: credentials.password})
          });
          const json = await response.json()
          console.log(json)
          if(json.success){
            localStorage.setItem('token',json.authtoken)
            props.showAlert("Logged in Successfully", "success")
            navigate("/")

          }
          else{
            props.showAlert("Invalid Credentials", "danger")
          }

    }

    const onchange = (e) => {
        setCredentials({ ...credentials, [e.target.name]: e.target.value })
    }

    return (
        <div style={{justifyContent: 'center',display: 'flex',alignItems: 'center'}}>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="email" className="form-label">Email address</label>
                    <input type="email" className="form-control" value={credentials.email} onChange={onchange} id="email" name='email' aria-describedby="emailHelp" style={{width:'300px'}}/>
                        <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                    <label htmlFor="password" className="form-label">Password</label>
                    <input type="password" className="form-control" value={credentials.password} onChange={onchange} id="password" name='password' style={{width:'300px'}}/>
                </div>
                <button type="submit" className="btn btn-primary"  >Submit</button>
            </form>
        </div>
    )
}

export default Login
