import React, { useState } from "react";
import NoteContext from "./noteContex";

const NoteState = (props) => {
  const host = "http://localhost:4000"
  const notesInitial = []

  const [notes, setNotes] = useState(notesInitial)

  // Get all notes
  const getnotes = async () => {

    const response = await fetch(`${host}/api/notes/fetchNotes`, {
      method: 'GET',
      headers: {
        'content-Type': 'application/json',
        'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRlYzNmYTc3ZDU2M2YwNTU2YTRhMTk1In0sImlhdCI6MTY5MzIwNDQ3NH0.Sv7_YgEwG9j1enxUUoDCyewplVr1lbjsZBBFCjFFWjM"
      },
    });
    const json = await response.json()
    setNotes(json)
    
  }

  // Add a note
  const Addnote = async (title, description, tag) => {

    const response = await fetch(`${host}/api/notes/addNote`, {
      method: 'POST',
      headers: {
        'content-Type': 'application/json',
        'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRlYzNmYTc3ZDU2M2YwNTU2YTRhMTk1In0sImlhdCI6MTY5MzIwNDQ3NH0.Sv7_YgEwG9j1enxUUoDCyewplVr1lbjsZBBFCjFFWjM"
      },
      body: JSON.stringify({title, description, tag})
    });
    const json = response.json()
    const note = {
      "_id": "64ec755a9t5b444b33087880a1a",
      "user": "64ec3fa77d563f0556a4a195",
      "title": title,
      "description": description,
      "tag": tag,
      "date": "2023-08-28T10:22:18.953Z",
      "__v": 0
    };
    setNotes(notes.concat(note))
  }

  // Delete a Note
  const DeleteNote =  async (id) => {
    const response = await fetch(`${host}/api/notes/deletenote/${id}`, {
      method: 'POST',
      headers: {
        'content-Type': 'application/json',
        'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRlYzNmYTc3ZDU2M2YwNTU2YTRhMTk1In0sImlhdCI6MTY5MzIwNDQ3NH0.Sv7_YgEwG9j1enxUUoDCyewplVr1lbjsZBBFCjFFWjM"
      },
      body: JSON.stringify({title, description, tag})
    });
    const json = response.json()
    const newnotes = notes.filter((note) => { return note._id !== id })
    setNotes(newnotes)
  }

  // Edit a note
  const EditNote = async (id, title, description, tag) => {
    // API Call

    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: 'POST',
      headers: {
        'content-Type': 'application/json',
        'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjRlYzNmYTc3ZDU2M2YwNTU2YTRhMTk1In0sImlhdCI6MTY5MzIwNDQ3NH0.Sv7_YgEwG9j1enxUUoDCyewplVr1lbjsZBBFCjFFWjM"
      },
      body: JSON.stringify({title, description, tag})
    });
    const json = response.json()

    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title
        element.description = description
        element.tag = tag
      }
    }
  }

  return (
    <NoteContext.Provider value={{ notes, setNotes, Addnote, DeleteNote, EditNote , getnotes}}>
      {props.children}
    </NoteContext.Provider>
  )
}

export default NoteState
